import logo from './logo.svg';
import './App.css';
import React, {useState, useEffect} from 'react';
import axios from 'axios';




import folderBrown from './images/folder-brown.ico';
import cog from './images/cog.ico';


class Nav extends React.Component {
  render() {
    return(
      <nav>
        <ul>
          <li><button>Наверх</button></li>
        </ul>
        <input type="text" className="" placeholder='Поиск' />
      </nav>
    )
  }
}

class FileBlock extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      file: props.file
    }
    this.selectImage = this.selectImage.bind(this)
  }


  componentDidUpdate () {
    if (this.state.file !== this.props.files) this.setState({ files: this.props.files })
  }

  selectImage() {
    switch (this.state.file.type) {
      case 'repos':
        return folderBrown
      break;
      default:
        return cog
      break;
    }

  }

  render() {
    return(
      <div className="block">
        <img src={this.selectImage()} alt="Icon" />


        <p>{this.state.file.name}</p>
      </div>
    )
  }
}


class List extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      files: props.files      
    }
  }

  componentDidUpdate () {
    if (this.state.files !== this.props.files) this.setState({ files: this.props.files })
  }
  render() {
    return (
      <div className="list">
        {this.state.files.map((file, index) => {
          return (
            <FileBlock file={file} key={index}/>
          )
        })}
      </div>
    )
  }

}




class App extends React.Component {
  constructor (props){
    super(props)
    this.state = {
      path: 'C:/Users/Zhe/Desktop/игры',
      files: [
        {
          name: 'папка раз',
          type: 'repos'
        },
        {
          name: 'папка dva',
          type: 'repos'
        },
        {
          name: 'папка tri',
          type: 'repos'
        },
        {
          name: 'файл один',
          type: 'txt'
        }
      ]
    }
  }

  componentDidMount() {
    console.log('начало запроса');
    axios.get('http://25.37.147.25:5000/', {params: {
      path: this.state.path
    }})
      .then(res => {
        if (!res) return console.eror('запрос не получен'); 
        const files = res.data.files
        this.setState({files})
        console.log('запрос получен');
        console.log(res);
      })
  }


  render() {
    return(
      <div className="App">
        <Nav />
        <List  files={this.state.files}/>
      </div>
    )
  }
}

export default App;
