import './App.css';
import React, {useState, useEffect} from 'react';
import axios from 'axios';




import folderBrown from './images/folder-brown.ico';
import cog from './images/cog.ico';


class Nav extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      onClick: this.props.onClick,
      path: this.props.path,
    }
    this.handleChange = this.handleChange.bind(this)
  }


  updateComponent () {
    if (this.state.path !== this.props.path) this.setState({ path: this.props.path })
  }

  handleChange(e){
    this.setState({ path: e.target.value})
  }

  render() {
    return(
      <nav>
        <ul>
          <li><button onClick={() => {this.state.onClick().then(); this.updateComponent()}}>Наверх</button></li>
        </ul>
        <span>{this.state.path}</span>
        <input type='text' value={this.state.path} onChange={this.handleChange} />
        <input type="text" className="" placeholder='Поиск' />
      </nav>
    )
  }
}

class FileBlock extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      file: props.file
    }
    this.selectImage = this.selectImage.bind(this)
  }


  componentDidUpdate () {
    if (this.state.file !== this.props.file) this.setState({ file: this.props.file })
  }

  selectImage() {
    switch (this.state.file.type) {
      case 'repos':
        return folderBrown
      default:
        return cog
    }

  }

  render() {
    return(
      <div className="block">
        <img src={this.selectImage()} alt="Icon" />


        <p>{this.state.file.toString()}</p>
      </div>
    )
  }
}


class List extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      files: props.files      
    }
  }

  componentDidUpdate () {
    if (this.state.files !== this.props.files) this.setState({ files: this.props.files })
  }
  render() {
    return (
      <div className="list">
        {this.state.files.map((file, index) => {
          return (
            <FileBlock file={file} key={index}/>
          )
        })}
      </div>
    )
  }

}




class App extends React.Component {
  constructor (props){
    super(props)
    this.state = {
      path: 'C:/Users/Zhe/Desktop/игры',
      files: []
    }
    this.handleUpFolder = this.handleUpFolder.bind(this)
  }


  handleUpFolder(){
    console.log('поднятие на папку вверх');
    const newPath = this.state.path.split( '/' ).slice( 0, -1 ).join( '/' );
    this.setState({path: newPath}, function(){this.getFiles()})
  }

  getFiles(){
    console.log('Запрос на ' + this.state.path);
    axios.get('http://25.37.147.25:5000/', {params: {
      path: this.state.path
    }})
      .then(res => {
        if (!res) return console.eror('запрос не получен'); 
        const files = res.data.files
        this.setState({files})
        console.log('запрос получен');
        console.log(res);
        console.log(typeof( res.data.files[10]));
      })

  }

  componentDidMount() {
    this.getFiles()    
  }


  render() {
    return(
      <div className="App">
        <Nav onClick={this.handleUpFolder} path={this.state.path} />
        <List  files={this.state.files}/>
      </div>
    )
  }
}

export default App;
