import './App.css';
import React, {useState, useEffect} from 'react';
import axios from 'axios';




import cog from './images/cog.ico';
import symLink from './image/emblem-symbolic-link.ico';


class Nav extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      onClick: this.props.onClick,
      onInput: this.props.onInput,
      path: this.props.path,
    }
    this.handleClick = this.handleClick.bind(this)
  }


  handleClick () {
    (async () => {
      this.state.onClick()
    })().then(() => {
      if (this.state.path !== this.props.path) this.setState({ path: this.props.path })
    })
  }


  render() {
    return(
      <nav className='flex-justify-between flex-align-center'>
        <ul className='list-none flex-justify-center'>
          <li><button onClick={this.handleClick}>Наверх</button></li>
          <li><button onClick={this.handleClick}>Наверх</button></li>
          <li><button onClick={this.handleClick}>Наверх</button></li>
        </ul>
        <input type='text' value={this.state.path} onInput={this.state.onInput} />
        <input type="text" className="" placeholder='Поиск' />
      </nav>
    )
  }
}

class FileBlock extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      file: props.file,
      onClick: props.onClick
    }
    this.selectImage = this.selectImage.bind(this)
  }


  componentDidUpdate () {
    if (this.state.file !== this.props.file) this.setState({ file: this.props.file })
  }

  selectImage() {
    const data = this.state.file.split('.')
    let type
    if (data.length === 1){
      type = 'repos'
    } else type = data.pop()
    switch (type) {
      case 'repos':
        return folderBrown
      case 'link':
        return symLink
      default:
        return cog
    }
  }

  typeOfFile(){
    console.log(this.state.file.split('.').pop());
   // const newPath = this.state.path.split( '/' ).slice( 0, -1 ).join( '/' );
  }

  render() {
    return(
      <div className="block" onClick={() => this.state.onClick(this.state.file)}>
        <img src={this.selectImage()} alt="Icon" />


        <p>{this.state.file.toString()}</p>
      </div>
    )
  }
}


class List extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      files: props.files,
      onClick: props.onClick   
    }
  }

  componentDidUpdate () {
    if (this.state.files !== this.props.files) this.setState({ files: this.props.files })
  }
  render() {
    return (
      <div className="flex-wrap">
        {this.state.files.map((file, index) => {
          return (
            <FileBlock file={file} key={index} onClick={this.state.onClick} />
          )
        })}
      </div>
    )
  }

}




class App extends React.Component {
  constructor (props){
    super(props)
    this.state = {
      path: 'C:/Users/Zhe/Desktop/игры',
      files: []
    }
    this.handleUpFolder = this.handleUpFolder.bind(this)
    this.handleBlockClick = this.handleBlockClick.bind(this)
    this.handleInput = this.handleInput.bind(this)
  }


  handleUpFolder(){
    console.log('поднятие на папку вверх');
    const newPath = this.state.path.split( '/' ).slice( 0, -1 ).join( '/' );
    this.getFiles(newPath)
  }

  handleBlockClick(file) {
    console.log('спуск в папку ' + file);
    const newPath = this.state.path + '/' + file
    this.getFiles(newPath)
  }

  handleInput(e) {
    this.setState({path: 'eee'})
  }

  getFiles(path){
    console.log('Запрос на ' + path);
    axios.get('http://25.37.147.25:5000/', {params: {
      path: path
    }})
      .then(res => {
        if (!res) return console.eror('запрос не получен'); 
        const files = res.data.files
        this.setState({files, path})
        console.log('запрос получен');
        console.log(res);
      })
      .catch(err => console.log( 'что-то пошло не так', err));
  }

  componentDidMount() {
    this.getFiles(this.state.path)    
  }

  render() {
    return(
      <div className="App">
        <Nav onClick={this.handleUpFolder} path={this.state.path} onInput={this.handleInput} />
        <List  files={this.state.files} onClick={this.handleBlockClick} />
      </div>
    )
  }
}

export default App;
