import logo from './logo.svg';
import './App.css';
import React from 'react';




class Nav extends React.Component {
  render() {
    return(
      <nav>
        <ul>
          <li><button>Наверх</button></li>
        </ul>
        <input type="text" className="" placeholder='Поиск' />
      </nav>
    )
  }
}

class FileBlock extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      file: props.file
    }
    this.selectImage = this.selectImage.bind(this)
  }

  selectImage() {
    switch (this.state.file.type) {
      case 'repos':
        return 'folder-brown.ico'
      break;
    }

  }

  render() {
    return(
      <div className="block">
        <img src={'../public/images/' + this.selectImage()} alt="Icon" />

        <p>{thisstate.file.name}</p>
      </div>
    )
  }
}


class List extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      files: props.files      
    }
  }
  render() {
    return (
      <div className="list">
        {this.state.files.map((file) => {
          return (
            <FileBlock file={file}/>
          )
        })}
      </div>
    )
  }

}




class App extends React.Component {
  constructor (props){
    super(props)
    this.state = {
      files: [
        {
          name: 'папка раз',
          type: 'repos'
        },
        {
          name: 'папка dva',
          type: 'repos'
        },
        {
          name: 'папка tri',
          type: 'repos'
        }
      ]
    }
  }
  render() {
    return(
      <div className="App">
        <Nav />
        <List  files={this.state.files}/>
      </div>
    )
  }
}

export default App;
