import './App.css';
import React, {useState, useEffect} from 'react';
import axios from 'axios';




import folderBrown from './images/folder-brown.ico';
import cog from './images/cog.ico';


class Nav extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      onClick: this.props.onClick,
      path: this.props.path,
      updateComponent: this.props.updateNav
    }
    this.handleChange = this.handleChange.bind(this)
    this.updateComponent = this.updateComponent.bind(this)
    this.updateComponent = this.updateComponent.bind(this)
  }


  updateComponent () {
    (async () => {
      this.state.onClick()
    })().then(() => {
      if (this.state.path !== this.props.path) this.setState({ path: this.props.path })
    })
  }

  handleChange(e){
    this.setState({ path: e.target.value})
  }

  render() {
    return(
      <nav className='flex-justify-between flex-align-center'>
        <ul className='list-none flex-justify-center'>
          <li><button onClick={this.updateComponent}>Наверх</button></li>
          <li><button onClick={this.updateComponent}>Наверх</button></li>
          <li><button onClick={this.updateComponent}>Наверх</button></li>
        </ul>
        <input type='text' value={this.state.path} onChange={this.handleChange} />
        <input type="text" className="" placeholder='Поиск' />
      </nav>
    )
  }
}

class FileBlock extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      file: props.file,
      onClick: props.onClick
    }
    this.selectImage = this.selectImage.bind(this)
  }


  componentDidUpdate () {
    if (this.state.file !== this.props.file) this.setState({ file: this.props.file })
  }

  selectImage() {
    switch (this.state.file.type) {
      case 'repos':
        return folderBrown
      default:
        return cog
    }

  }

  render() {
    return(
      <div className="block" onClick={() => this.state.onClick(this.state.file)}>
        <img src={this.selectImage()} alt="Icon" />


        <p>{this.state.file.toString()}</p>
      </div>
    )
  }
}


class List extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      files: props.files,
      onClick: props.onClick   
    }
  }

  componentDidUpdate () {
    if (this.state.files !== this.props.files) this.setState({ files: this.props.files })
  }
  render() {
    return (
      <div className="flex-wrap">
        {this.state.files.map((file, index) => {
          return (
            <FileBlock file={file} key={index} onClick={this.state.onClick} />
          )
        })}
      </div>
    )
  }

}




class App extends React.Component {
  constructor (props){
    super(props)
    this.state = {
      path: 'C:/Users/Zhe/Desktop/игры',
      files: []
    }
    this.handleUpFolder = this.handleUpFolder.bind(this)
    this.handleBlockClick = this.handleBlockClick.bind(this)
  }


  handleUpFolder(){
    console.log('поднятие на папку вверх');
    const newPath = this.state.path.split( '/' ).slice( 0, -1 ).join( '/' );
    this.getFiles(newPath)
  }

  handleBlockClick(file) {
    console.log('спуск в папку ' + file);
    const newPath = this.state.path + '/' + file
    this.getFiles(newPath)
  }


  getFiles(path){
    console.log('Запрос на ' + path);
    axios.get('http://25.37.147.25:5000/', {params: {
      path: path
    }})
      .then(res => {
        if (!res) return console.eror('запрос не получен'); 
        const files = res.data.files
        this.setState({files, path})
        console.log('запрос получен');
        console.log(res);
      })
  }

  componentDidMount() {
    this.getFiles(this.state.path)    
  }

  updateNav() {

  }


  render() {
    return(
      <div className="App">
        <Nav onClick={this.handleUpFolder} path={this.state.path} />
        <List  files={this.state.files} onClick={this.handleBlockClick} updateNav={this.updateNav} />
      </div>
    )
  }
}

export default App;
